mapper.update:

 	SELECT	q_idx as #{qIdx}
 	FROM	y_question
		WHERE	q_type = 2 AND s_idx = #{sIdx}

 UPDATE	y_option
 SET		q_idx = #{qIdx}
 WHERE	s_idx = #{sIdx}

mapper.insert:
		INSERT INTO y_allanswer(
			s_title,
			s_idx
		)
		VALUES (
        	#{sTitle},
        	#{sIdx}
        )

mapper.update:
UPDATE	y_question
SET		q_type = #{qType},
		q_question = #{qQuestion}	
WHERE	s_idx = #{sIdx} and q_idx = #{qIdx}

UPDATE	y_option
SET		o_option = #{oOption},
		s_idx = #{sIdx},
		q_idx = #{qIdx}
WHERE	s_idx = #{sIdx} and o_idx = #{oIdx}
